
public class DriverCls {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//for loop
		for (int i=0;i<10;i++)
			System.out.print(i+" ");
		int j=12;
		for (;j<20;j++)
			System.out.print(j+",");
		for (int z=21;z<31;) {
			System.out.print(z+"-");
			z++;
		}
		System.out.print("\n");
		
		//while loop
		int a=1;
		while(a<11) {
			System.out.println("6x"+a+"="+6*a);
			a++;
		}
		
	}

}
